using System.Collections; using System.Collections.Generic; using UnityEngine;  public class NewBehaviourScript : MonoBehaviour {     // Start is called before the first frame update     void Start()     {              }      // Update is called once per frame     void Update()     {              } } public class MainActivity : FormsAppCompatActivity {     internal static MainActivity Instance { get; private set; }      protected override void OnCreate(AssetBundle savedInstanceState) =>         // ...         Instance = this;     // ...     // Field, property, and method for Picture Picker     public static readonly int PickImageId = 1000;      public System.Threading.Tasks.TaskCompletionSource<StreamingController> PickImageTaskCompletionSource { set; get; }      protected override void OnActivityResult(int requestCode, AssetBundleLoadResult resultCode, Intent intent)     {         base.OnActivityResult(requestCode, resultCode, intent);          if (requestCode == PickImageId)         {             if ((resultCode == resultCode) && (intent != null))             {                 AndroidJNI.Net.Uri uri = intent.Data;                 StreamingController stream = ContentResolver.OpenInputStream(uri);                  // Set the Stream as the completion of the Task                 PickImageTaskCompletionSource.SetResult(stream);             }             else             {                 PickImageTaskCompletionSource.SetResult(null);             }         }     } }